declare const styles = "\n .maxtap_container {\n    position: relative;\n    margin: inherit;\n    width: inherit;\n    padding: inherit;\n    z-index: inherit;\n    box-sizing: border-box;\n    display: flex;\n    flex-direction: column;\n}\n\n.maxtap_container video {\n    width: 100%;\n    height: 100%;\n}\n\n.maxtap_component_wrapper {\n    align-self: flex-end;\n    position: absolute;\n    right: 0px;\n    bottom: calc(6vw + 6vh);\n    display: flex;\n}\n.maxtap_main{\n    display: flex;\n    flex-direction: row;\n    align-items: center;\n    justify-content: space-between;\n    height: fit-content;\n    background-color: hsla(0, 0%, 0%, 0.2);\n    cursor: pointer;\n    z-index: 10;\n}\n.maxtap_img_wrapper {\n    margin-left: 0.6rem;\n    display: flex;\n    justify-content: center;\n    align-items: center;\n    padding: 0.3vw;\n    width: 5vw;\n}\n.maxtap_img_wrapper > img{\n    width: 100%;\n}\n.maxtap_main>p {\n    font-family: ubuntu,\"Roboto\",sans-serif,Arial, Helvetica, sans-serif;\n    font-weight: 500;\n    font-size: calc(1vw + 0.1rem);\n    padding-left: 0.4rem;\n    color: white;\n}\n ";
